INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 1, 'X.b Periode Kedua Pelayanan Besar di Galilea');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 2, 'X.b.1 Pelayanan di Galilea Berlanjut');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 3, '52. Yesus Semakin Terkenal');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 4, 'Matius 12');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 5, '');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 6, '15 Tetapi Yesus mengetahui maksud mereka lalu menyingkir dari sana. (12-15b) Banyak orang mengikuti Yesus dan Ia menyembuhkan mereka semuanya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 7, '16 Ia dengan keras melarang mereka memberitahukan siapa Dia,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 8, '17 supaya genaplah firman yang disampaikan oleh nabi Yesaya:');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 9, '18 "Lihatlah, itu Hamba-Ku yang Kupilih, yang Kukasihi, yang kepada-Nya jiwa-Ku berkenan; Aku akan menaruh roh-Ku ke atas-Nya, dan Ia akan memaklumkan hukum kepada bangsa-bangsa.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 10, '19 Ia tidak akan berbantah dan tidak akan berteriak dan orang tidak akan mendengar suara-Nya di jalan-jalan.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 11, '20 Buluh yang patah terkulai tidak akan diputuskan-Nya, dan sumbu yang pudar nyalanya tidak akan dipadamkan-Nya, sampai Ia menjadikan hukum itu menang.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 12, '21 Dan pada-Nyalah bangsa-bangsa akan berharap."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 13, '');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 14, 'Markus 3');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 15, '');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 16, '7 Kemudian Yesus dengan murid-murid-Nya menyingkir ke danau, dan banyak orang dari Galilea mengikuti-Nya. Juga dari Yudea,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 17, '8 dari Yerusalem, dari Idumea, dari seberang Yordan, dan dari daerah Tirus dan Sidon datang banyak orang kepada-Nya, sesudah mereka mendengar segala yang dilakukan-Nya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 18, '9 Ia menyuruh murid-murid-Nya menyediakan sebuah perahu bagi-Nya karena orang banyak itu, supaya mereka jangan sampai menghimpit-Nya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 19, '10 Sebab Ia menyembuhkan banyak orang, sehingga semua penderita penyakit berdesak-desakan kepada-Nya hendak menjamah-Nya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 20, '11 Bilamana roh-roh jahat melihat Dia, mereka jatuh tersungkur di hadapan-Nya dan berteriak: "Engkaulah Anak Allah."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 21, '12 Tetapi Ia dengan keras melarang mereka memberitahukan siapa Dia.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 22, 'Yesus Mengangkat Kedua-belas Rasul');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 23, 'Lukas 6');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 24, '');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 25, '12 Pada waktu itu pergilah Yesus ke bukit untuk berdoa dan semalam-malaman Ia berdoa kepada Allah.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 26, '13 Ketika hari siang, Ia memanggil murid-murid-Nya kepada-Nya, lalu memilih dari antara mereka dua belas orang, yang disebut-Nya rasul:');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 27, '14 Simon yang juga diberi-Nya nama Petrus, dan Andreas saudara Simon, Yakobus dan Yohanes, Filipus dan Bartolomeus,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 28, '15 Matius dan Tomas, Yakobus anak Alfeus, dan Simon yang disebut orang Zelot,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 29, '16 Yudas anak Yakobus, dan Yudas Iskariot yang kemudian menjadi pengkhianat.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 30, '');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 31, 'Matius 10');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 32, '');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 33, '2 Inilah nama kedua belas rasul itu: Pertama Simon yang disebut Petrus dan Andreas saudaranya, dan Yakobus anak Zebedeus dan Yohanes saudaranya,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 34, '3 Filipus dan Bartolomeus, Tomas dan Matius pemungut cukai, Yakobus anak Alfeus, dan Tadeus,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 35, '4 Simon orang Zelot dan Yudas Iskariot yang mengkhianati Dia.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 36, '');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 37, 'Markus 3');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 38, '');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 39, '13 Kemudian naiklah Yesus ke atas bukit. Ia memanggil orang-orang yang dikehendaki-Nya dan merekapun datang kepada-Nya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 40, '14 Ia menetapkan dua belas orang untuk menyertai Dia dan untuk diutus-Nya memberitakan Injil');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 41, '15 dan diberi-Nya kuasa untuk mengusir setan.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 42, '16 Kedua belas orang yang ditetapkan-Nya itu ialah: Simon, yang diberi-Nya nama Petrus,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 43, '17 Yakobus anak Zebedeus, dan Yohanes saudara Yakobus, yang keduanya diberi-Nya nama Boanerges, yang berarti anak-anak guruh,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 44, '18 selanjutnya Andreas, Filipus, Bartolomeus, Matius, Tomas, Yakobus anak Alfeus, Tadeus, Simon orang Zelot,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 45, '19 dan Yudas Iskariot, yang mengkhianati Dia.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 46, 'Khotbah di Bukit');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 47, 'Matius 5');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 48, '');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 49, '1 Ketika Yesus melihat orang banyak itu, naiklah Ia ke atas bukit dan setelah Ia duduk, datanglah murid-murid-Nya kepada-Nya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 50, '2 Maka Yesuspun mulai berbicara dan mengajar mereka, kata-Nya:');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 51, '3 "Berbahagialah orang yang miskin di hadapan Allah, karena merekalah yang empunya Kerajaan Sorga.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 52, '4 Berbahagialah orang yang berdukacita, karena mereka akan dihibur.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 53, '5 Berbahagialah orang yang lemah lembut, karena mereka akan memiliki bumi.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 54, '6 Berbahagialah orang yang lapar dan haus akan kebenaran, karena mereka akan dipuaskan.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 55, '7 Berbahagialah orang yang murah hatinya, karena mereka akan beroleh kemurahan.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 56, '8 Berbahagialah orang yang suci hatinya, karena mereka akan melihat Allah.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 57, '9 Berbahagialah orang yang membawa damai, karena mereka akan disebut anak-anak Allah.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 58, '10 Berbahagialah orang yang dianiaya oleh sebab kebenaran, karena merekalah yang empunya Kerajaan Sorga.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 59, '11 Berbahagialah kamu, jika karena Aku kamu dicela dan dianiaya dan kepadamu difitnahkan segala yang jahat.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 60, '12 Bersukacita dan bergembiralah, karena upahmu besar di sorga, sebab demikian juga telah dianiaya nabi-nabi yang sebelum kamu."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 61, '13 "Kamu adalah garam dunia. Jika garam itu menjadi tawar, dengan apakah ia diasinkan? Tidak ada lagi gunanya selain dibuang dan diinjak orang.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 62, '14 Kamu adalah terang dunia. Kota yang terletak di atas gunung tidak mungkin tersembunyi.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 63, '15 Lagipula orang tidak menyalakan pelita lalu meletakkannya di bawah gantang, melainkan di atas kaki dian sehingga menerangi semua orang di dalam rumah itu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 64, '16 Demikianlah hendaknya terangmu bercahaya di depan orang, supaya mereka melihat perbuatanmu yang baik dan memuliakan Bapamu yang di sorga."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 65, '17 "Janganlah kamu menyangka, bahwa Aku datang untuk meniadakan hukum Taurat atau kitab para nabi. Aku datang bukan untuk meniadakannya, melainkan untuk menggenapinya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 66, '18 Karena Aku berkata kepadamu: Sesungguhnya selama belum lenyap langit dan bumi ini, satu iota atau satu titikpun tidak akan ditiadakan dari hukum Taurat, sebelum semuanya terjadi.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 67, '19 Karena itu siapa yang meniadakan salah satu perintah hukum Taurat sekalipun yang paling kecil, dan mengajarkannya demikian kepada orang lain, ia akan menduduki tempat yang paling rendah di dalam Kerajaan Sorga; tetapi siapa yang melakukan dan mengajarkan segala perintah-perintah hukum Taurat, ia akan menduduki tempat yang tinggi di dalam Kerajaan Sorga.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 68, '20 Maka Aku berkata kepadamu: Jika hidup keagamaanmu tidak lebih benar dari pada hidup keagamaan ahli-ahli Taurat dan orang-orang Farisi, sesungguhnya kamu tidak akan masuk ke dalam Kerajaan Sorga.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 69, '21 Kamu telah mendengar yang difirmankan kepada nenek moyang kita: Jangan membunuh; siapa yang membunuh harus dihukum.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 70, '22 Tetapi Aku berkata kepadamu: Setiap orang yang marah terhadap saudaranya harus dihukum; siapa yang berkata kepada saudaranya: Kafir! harus dihadapkan ke Mahkamah Agama dan siapa yang berkata: Jahil! harus diserahkan ke dalam neraka yang menyala-nyala.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 71, '23 Sebab itu, jika engkau mempersembahkan persembahanmu di atas mezbah dan engkau teringat akan sesuatu yang ada dalam hati saudaramu terhadap engkau,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 72, '24 tinggalkanlah persembahanmu di depan mezbah itu dan pergilah berdamai dahulu dengan saudaramu, lalu kembali untuk mempersembahkan persembahanmu itu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 73, '25 Segeralah berdamai dengan lawanmu selama engkau bersama-sama dengan dia di tengah jalan, supaya lawanmu itu jangan menyerahkan engkau kepada hakim dan hakim itu menyerahkan engkau kepada pembantunya dan engkau dilemparkan ke dalam penjara.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 74, '26 Aku berkata kepadamu: Sesungguhnya engkau tidak akan keluar dari sana, sebelum engkau membayar hutangmu sampai lunas.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 75, '27 Kamu telah mendengar firman: Jangan berzinah.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 76, '28 Tetapi Aku berkata kepadamu: Setiap orang yang memandang perempuan serta menginginkannya, sudah berzinah dengan dia di dalam hatinya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 77, '29 Maka jika matamu yang kanan menyesatkan engkau, cungkillah dan buanglah itu, karena lebih baik bagimu jika satu dari anggota tubuhmu binasa, dari pada tubuhmu dengan utuh dicampakkan ke dalam neraka.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 78, '30 Dan jika tanganmu yang kanan menyesatkan engkau, penggallah dan buanglah itu, karena lebih baik bagimu jika satu dari anggota tubuhmu binasa dari pada tubuhmu dengan utuh masuk neraka.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 79, '31 Telah difirmankan juga: Siapa yang menceraikan isterinya harus memberi surat cerai kepadanya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 80, '32 Tetapi Aku berkata kepadamu: Setiap orang yang menceraikan isterinya kecuali karena zinah, ia menjadikan isterinya berzinah; dan siapa yang kawin dengan perempuan yang diceraikan, ia berbuat zinah.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 81, '33 Kamu telah mendengar pula yang difirmankan kepada nenek moyang kita: Jangan bersumpah palsu, melainkan peganglah sumpahmu di depan Tuhan.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 82, '34 Tetapi Aku berkata kepadamu: Janganlah sekali-kali bersumpah, baik demi langit, karena langit adalah takhta Allah,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 83, '35 maupun demi bumi, karena bumi adalah tumpuan kaki-Nya, ataupun demi Yerusalem, karena Yerusalem adalah kota Raja Besar;');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 84, '36 janganlah juga engkau bersumpah demi kepalamu, karena engkau tidak berkuasa memutihkan atau menghitamkan sehelai rambutpun.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 85, '37 Jika ya, hendaklah kamu katakan: ya, jika tidak, hendaklah kamu katakan: tidak. Apa yang lebih dari pada itu berasal dari si jahat.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 86, '38 Kamu telah mendengar firman: Mata ganti mata dan gigi ganti gigi.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 87, '39 Tetapi Aku berkata kepadamu: Janganlah kamu melawan orang yang berbuat jahat kepadamu, melainkan siapapun yang menampar pipi kananmu, berilah juga kepadanya pipi kirimu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 88, '40 Dan kepada orang yang hendak mengadukan engkau karena mengingini bajumu, serahkanlah juga jubahmu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 89, '41 Dan siapapun yang memaksa engkau berjalan sejauh satu mil, berjalanlah bersama dia sejauh dua mil.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 90, '42 Berilah kepada orang yang meminta kepadamu dan janganlah menolak orang yang mau meminjam dari padamu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 91, '43 Kamu telah mendengar firman: Kasihilah sesamamu manusia dan bencilah musuhmu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 92, '44 Tetapi Aku berkata kepadamu: Kasihilah musuhmu dan berdoalah bagi mereka yang menganiaya kamu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 93, '45 Karena dengan demikianlah kamu menjadi anak-anak Bapamu yang di sorga, yang menerbitkan matahari bagi orang yang jahat dan orang yang baik dan menurunkan hujan bagi orang yang benar dan orang yang tidak benar.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 94, '46 Apabila kamu mengasihi orang yang mengasihi kamu, apakah upahmu? Bukankah pemungut cukai juga berbuat demikian?');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 95, '47 Dan apabila kamu hanya memberi salam kepada saudara-saudaramu saja, apakah lebihnya dari pada perbuatan orang lain? Bukankah orang yang tidak mengenal Allahpun berbuat demikian?');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 96, '48 Karena itu haruslah kamu sempurna, sama seperti Bapamu yang di sorga adalah sempurna."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 97, '');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 98, 'Matius 6');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 99, '');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 100, '1 "Ingatlah, jangan kamu melakukan kewajiban agamamu di hadapan orang supaya dilihat mereka, karena jika demikian, kamu tidak beroleh upah dari Bapamu yang di sorga.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 101, '2 Jadi apabila engkau memberi sedekah, janganlah engkau mencanangkan hal itu, seperti yang dilakukan orang munafik di rumah-rumah ibadat dan di lorong-lorong, supaya mereka dipuji orang. Aku berkata kepadamu: Sesungguhnya mereka sudah mendapat upahnya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 102, '3 Tetapi jika engkau memberi sedekah, janganlah diketahui tangan kirimu apa yang diperbuat tangan kananmu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 103, '4 Hendaklah sedekahmu itu diberikan dengan tersembunyi, maka Bapamu yang melihat yang tersembunyi akan membalasnya kepadamu."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 104, '5 "Dan apabila kamu berdoa, janganlah berdoa seperti orang munafik. Mereka suka mengucapkan doanya dengan berdiri dalam rumah-rumah ibadat dan pada tikungan-tikungan jalan raya, supaya mereka dilihat orang. Aku berkata kepadamu: Sesungguhnya mereka sudah mendapat upahnya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 105, '6 Tetapi jika engkau berdoa, masuklah ke dalam kamarmu, tutuplah pintu dan berdoalah kepada Bapamu yang ada di tempat tersembunyi. Maka Bapamu yang melihat yang tersembunyi akan membalasnya kepadamu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 106, '7 Lagipula dalam doamu itu janganlah kamu bertele-tele seperti kebiasaan orang yang tidak mengenal Allah. Mereka menyangka bahwa karena banyaknya kata-kata doanya akan dikabulkan.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 107, '8 Jadi janganlah kamu seperti mereka, karena Bapamu mengetahui apa yang kamu perlukan, sebelum kamu minta kepada-Nya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 108, '9 Karena itu berdoalah demikian: Bapa kami yang di sorga, Dikuduskanlah nama-Mu,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 109, '10 datanglah Kerajaan-Mu, jadilah kehendak-Mu di bumi seperti di sorga.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 110, '11 Berikanlah kami pada hari ini makanan kami yang secukupnya');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 111, '12 dan ampunilah kami akan kesalahan kami, seperti kami juga mengampuni orang yang bersalah kepada kami;');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 112, '13 dan janganlah membawa kami ke dalam pencobaan, tetapi lepaskanlah kami dari pada yang jahat. (Karena Engkaulah yang empunya Kerajaan dan kuasa dan kemuliaan sampai selama-lamanya. Amin.)');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 113, '14 Karena jikalau kamu mengampuni kesalahan orang, Bapamu yang di sorga akan mengampuni kamu juga.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 114, '15 Tetapi jikalau kamu tidak mengampuni orang, Bapamu juga tidak akan mengampuni kesalahanmu."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 115, '16 "Dan apabila kamu berpuasa, janganlah muram mukamu seperti orang munafik. Mereka mengubah air mukanya, supaya orang melihat bahwa mereka sedang berpuasa. Aku berkata kepadamu: Sesungguhnya mereka sudah mendapat upahnya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 116, '17 Tetapi apabila engkau berpuasa, minyakilah kepalamu dan cucilah mukamu,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 117, '18 supaya jangan dilihat oleh orang bahwa engkau sedang berpuasa, melainkan hanya oleh Bapamu yang ada di tempat tersembunyi. Maka Bapamu yang melihat yang tersembunyi akan membalasnya kepadamu."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 118, '19 "Janganlah kamu mengumpulkan harta di bumi; di bumi ngengat dan karat merusakkannya dan pencuri membongkar serta mencurinya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 119, '20 Tetapi kumpulkanlah bagimu harta di sorga; di sorga ngengat dan karat tidak merusakkannya dan pencuri tidak membongkar serta mencurinya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 120, '21 Karena di mana hartamu berada, di situ juga hatimu berada.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 121, '22 Mata adalah pelita tubuh. Jika matamu baik, teranglah seluruh tubuhmu;');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 122, '23 jika matamu jahat, gelaplah seluruh tubuhmu. Jadi jika terang yang ada padamu gelap, betapa gelapnya kegelapan itu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 123, '24 Tak seorangpun dapat mengabdi kepada dua tuan. Karena jika demikian, ia akan membenci yang seorang dan mengasihi yang lain, atau ia akan setia kepada yang seorang dan tidak mengindahkan yang lain. Kamu tidak dapat mengabdi kepada Allah dan kepada Mamon."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 124, '25 "Karena itu Aku berkata kepadamu: Janganlah kuatir akan hidupmu, akan apa yang hendak kamu makan atau minum, dan janganlah kuatir pula akan tubuhmu, akan apa yang hendak kamu pakai. Bukankah hidup itu lebih penting dari pada makanan dan tubuh itu lebih penting dari pada pakaian?');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 125, '26 Pandanglah burung-burung di langit, yang tidak menabur dan tidak menuai dan tidak mengumpulkan bekal dalam lumbung, namun diberi makan oleh Bapamu yang di sorga. Bukankah kamu jauh melebihi burung-burung itu?');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 126, '27 Siapakah di antara kamu yang karena kekuatirannya dapat menambahkan sehasta saja pada jalan hidupnya?');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 127, '28 Dan mengapa kamu kuatir akan pakaian? Perhatikanlah bunga bakung di ladang, yang tumbuh tanpa bekerja dan tanpa memintal,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 128, '29 namun Aku berkata kepadamu: Salomo dalam segala kemegahannyapun tidak berpakaian seindah salah satu dari bunga itu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 129, '30 Jadi jika demikian Allah mendandani rumput di ladang, yang hari ini ada dan besok dibuang ke dalam api, tidakkah Ia akan terlebih lagi mendandani kamu, hai orang yang kurang percaya?');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 130, '31 Sebab itu janganlah kamu kuatir dan berkata: Apakah yang akan kami makan? Apakah yang akan kami minum? Apakah yang akan kami pakai?');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 131, '32 Semua itu dicari bangsa-bangsa yang tidak mengenal Allah. Akan tetapi Bapamu yang di sorga tahu, bahwa kamu memerlukan semuanya itu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 132, '33 Tetapi carilah dahulu Kerajaan Allah dan kebenarannya, maka semuanya itu akan ditambahkan kepadamu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 133, '34 Sebab itu janganlah kamu kuatir akan hari besok, karena hari besok mempunyai kesusahannya sendiri. Kesusahan sehari cukuplah untuk sehari."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 134, '');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 135, 'Matius 7');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 136, '');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 137, '1 "Jangan kamu menghakimi, supaya kamu tidak dihakimi.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 138, '2 Karena dengan penghakiman yang kamu pakai untuk menghakimi, kamu akan dihakimi dan ukuran yang kamu pakai untuk mengukur, akan diukurkan kepadamu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 139, '3 Mengapakah engkau melihat selumbar di mata saudaramu, sedangkan balok di dalam matamu tidak engkau ketahui?');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 140, '4 Bagaimanakah engkau dapat berkata kepada saudaramu: Biarlah aku mengeluarkan selumbar itu dari matamu, padahal ada balok di dalam matamu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 141, '5 Hai orang munafik, keluarkanlah dahulu balok dari matamu, maka engkau akan melihat dengan jelas untuk mengeluarkan selumbar itu dari mata saudaramu."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 142, '6 "Jangan kamu memberikan barang yang kudus kepada anjing dan jangan kamu melemparkan mutiaramu kepada babi, supaya jangan diinjak-injaknya dengan kakinya, lalu ia berbalik mengoyak kamu."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 143, '7 "Mintalah, maka akan diberikan kepadamu; carilah, maka kamu akan mendapat; ketoklah, maka pintu akan dibukakan bagimu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 144, '8 Karena setiap orang yang meminta, menerima dan setiap orang yang mencari, mendapat dan setiap orang yang mengetok, baginya pintu dibukakan.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 145, '9 Adakah seorang dari padamu yang memberi batu kepada anaknya, jika ia meminta roti,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 146, '10 atau memberi ular, jika ia meminta ikan?');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 147, '11 Jadi jika kamu yang jahat tahu memberi pemberian yang baik kepada anak-anakmu, apalagi Bapamu yang di sorga! Ia akan memberikan yang baik kepada mereka yang meminta kepada-Nya."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 148, '12 "Segala sesuatu yang kamu kehendaki supaya orang perbuat kepadamu, perbuatlah demikian juga kepada mereka. Itulah isi seluruh hukum Taurat dan kitab para nabi.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 149, '13 Masuklah melalui pintu yang sesak itu, karena lebarlah pintu dan luaslah jalan yang menuju kepada kebinasaan, dan banyak orang yang masuk melaluinya;');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 150, '14 karena sesaklah pintu dan sempitlah jalan yang menuju kepada kehidupan, dan sedikit orang yang mendapatinya."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 151, '15 "Waspadalah terhadap nabi-nabi palsu yang datang kepadamu dengan menyamar seperti domba, tetapi sesungguhnya mereka adalah serigala yang buas.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 152, '16 Dari buahnyalah kamu akan mengenal mereka. Dapatkah orang memetik buah anggur dari semak duri atau buah ara dari rumput duri?');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 153, '17 Demikianlah setiap pohon yang baik menghasilkan buah yang baik, sedang pohon yang tidak baik menghasilkan buah yang tidak baik.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 154, '18 Tidak mungkin pohon yang baik itu menghasilkan buah yang tidak baik, ataupun pohon yang tidak baik itu menghasilkan buah yang baik.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 155, '19 Dan setiap pohon yang tidak menghasilkan buah yang baik, pasti ditebang dan dibuang ke dalam api.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 156, '20 Jadi dari buahnyalah kamu akan mengenal mereka.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 157, '21 Bukan setiap orang yang berseru kepada-Ku: Tuhan, Tuhan! akan masuk ke dalam Kerajaan Sorga, melainkan dia yang melakukan kehendak Bapa-Ku yang di sorga.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 158, '22 Pada hari terakhir banyak orang akan berseru kepada-Ku: Tuhan, Tuhan, bukankah kami bernubuat demi nama-Mu, dan mengusir setan demi nama-Mu, dan mengadakan banyak mujizat demi nama-Mu juga?');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 159, '23 Pada waktu itulah Aku akan berterus terang kepada mereka dan berkata: Aku tidak pernah mengenal kamu! Enyahlah dari pada-Ku, kamu sekalian pembuat kejahatan!"');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 160, '24 "Setiap orang yang mendengar perkataan-Ku ini dan melakukannya, ia sama dengan orang yang bijaksana, yang mendirikan rumahnya di atas batu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 161, '25 Kemudian turunlah hujan dan datanglah banjir, lalu angin melanda rumah itu, tetapi rumah itu tidak rubuh sebab didirikan di atas batu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 162, '26 Tetapi setiap orang yang mendengar perkataan-Ku ini dan tidak melakukannya, ia sama dengan orang yang bodoh, yang mendirikan rumahnya di atas pasir.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 163, '27 Kemudian turunlah hujan dan datanglah banjir, lalu angin melanda rumah itu, sehingga rubuhlah rumah itu dan hebatlah kerusakannya."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 164, '28 Dan setelah Yesus mengakhiri perkataan ini, takjublah orang banyak itu mendengar pengajaran-Nya,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 165, '29 sebab Ia mengajar mereka sebagai orang yang berkuasa, tidak seperti ahli-ahli Taurat mereka.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 166, '');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 167, 'Matius 8');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 168, '');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 169, '1 Setelah Yesus turun dari bukit, orang banyak berbondong-bondong mengikuti Dia.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 170, '');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 171, 'Lukas 6');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 172, '');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 173, '17 Lalu Ia turun dengan mereka dan berhenti pada suatu tempat yang datar: di situ berkumpul sejumlah besar dari murid-murid-Nya dan banyak orang lain yang datang dari seluruh Yudea dan dari Yerusalem dan dari daerah pantai Tirus dan Sidon.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 174, '18 Mereka datang untuk mendengarkan Dia dan untuk disembuhkan dari penyakit mereka; juga mereka yang dirasuk oleh roh-roh jahat beroleh kesembuhan.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 175, '19 Dan semua orang banyak itu berusaha menjamah Dia, karena ada kuasa yang keluar dari pada-Nya dan semua orang itu disembuhkan-Nya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 176, '20 Lalu Yesus memandang murid-murid-Nya dan berkata: "Berbahagialah, hai kamu yang miskin, karena kamulah yang empunya Kerajaan Allah.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 177, '21 Berbahagialah, hai kamu yang sekarang ini lapar, karena kamu akan dipuaskan. Berbahagialah, hai kamu yang sekarang ini menangis, karena kamu akan tertawa.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 178, '22 Berbahagialah kamu, jika karena Anak Manusia orang membenci kamu, dan jika mereka mengucilkan kamu, dan mencela kamu serta menolak namamu sebagai sesuatu yang jahat.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 179, '23 Bersukacitalah pada waktu itu dan bergembiralah, sebab sesungguhnya, upahmu besar di sorga; karena secara demikian juga nenek moyang mereka telah memperlakukan para nabi.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 180, '24 Tetapi celakalah kamu, hai kamu yang kaya, karena dalam kekayaanmu kamu telah memperoleh penghiburanmu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 181, '25 Celakalah kamu, yang sekarang ini kenyang, karena kamu akan lapar. Celakalah kamu, yang sekarang ini tertawa, karena kamu akan berdukacita dan menangis.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 182, '26 Celakalah kamu, jika semua orang memuji kamu; karena secara demikian juga nenek moyang mereka telah memperlakukan nabi-nabi palsu."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 183, '27 "Tetapi kepada kamu, yang mendengarkan Aku, Aku berkata: Kasihilah musuhmu, berbuatlah baik kepada orang yang membenci kamu;');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 184, '28 mintalah berkat bagi orang yang mengutuk kamu; berdoalah bagi orang yang mencaci kamu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 185, '29 Barangsiapa menampar pipimu yang satu, berikanlah juga kepadanya pipimu yang lain, dan barangsiapa yang mengambil jubahmu, biarkan juga ia mengambil bajumu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 186, '30 Berilah kepada setiap orang yang meminta kepadamu; dan janganlah meminta kembali kepada orang yang mengambil kepunyaanmu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 187, '31 Dan sebagaimana kamu kehendaki supaya orang perbuat kepadamu, perbuatlah juga demikian kepada mereka.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 188, '32 Dan jikalau kamu mengasihi orang yang mengasihi kamu, apakah jasamu? Karena orang-orang berdosapun mengasihi juga orang-orang yang mengasihi mereka.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 189, '33 Sebab jikalau kamu berbuat baik kepada orang yang berbuat baik kepada kamu, apakah jasamu? Orang-orang berdosapun berbuat demikian.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 190, '34 Dan jikalau kamu meminjamkan sesuatu kepada orang, karena kamu berharap akan menerima sesuatu dari padanya, apakah jasamu? Orang-orang berdosapun meminjamkan kepada orang-orang berdosa, supaya mereka menerima kembali sama banyak.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 191, '35 Tetapi kamu, kasihilah musuhmu dan berbuatlah baik kepada mereka dan pinjamkan dengan tidak mengharapkan balasan, maka upahmu akan besar dan kamu akan menjadi anak-anak Allah Yang Mahatinggi, sebab Ia baik terhadap orang-orang yang tidak tahu berterima kasih dan terhadap orang-orang jahat.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 192, '36 Hendaklah kamu murah hati, sama seperti Bapamu adalah murah hati."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 193, '37 "Janganlah kamu menghakimi, maka kamupun tidak akan dihakimi. Dan janganlah kamu menghukum, maka kamupun tidak akan dihukum; ampunilah dan kamu akan diampuni.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 194, '38 Berilah dan kamu akan diberi: suatu takaran yang baik, yang dipadatkan, yang digoncang dan yang tumpah ke luar akan dicurahkan ke dalam ribaanmu. Sebab ukuran yang kamu pakai untuk mengukur, akan diukurkan kepadamu."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 195, '39 Yesus mengatakan pula suatu perumpamaan kepada mereka: "Dapatkah orang buta menuntun orang buta? Bukankah keduanya akan jatuh ke dalam lobang?');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 196, '40 Seorang murid tidak lebih dari pada gurunya, tetapi barangsiapa yang telah tamat pelajarannya akan sama dengan gurunya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 197, '41 Mengapakah engkau melihat selumbar di dalam mata saudaramu, sedangkan balok di dalam matamu sendiri tidak engkau ketahui?');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 198, '42 Bagaimanakah engkau dapat berkata kepada saudaramu: Saudara, biarlah aku mengeluarkan selumbar yang ada di dalam matamu, padahal balok yang di dalam matamu tidak engkau lihat? Hai orang munafik, keluarkanlah dahulu balok dari matamu, maka engkau akan melihat dengan jelas untuk mengeluarkan selumbar itu dari mata saudaramu."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 199, '43 "Karena tidak ada pohon yang baik yang menghasilkan buah yang tidak baik, dan juga tidak ada pohon yang tidak baik yang menghasilkan buah yang baik.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 200, '44 Sebab setiap pohon dikenal pada buahnya. Karena dari semak duri orang tidak memetik buah ara dan dari duri-duri tidak memetik buah anggur.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 201, '45 Orang yang baik mengeluarkan barang yang baik dari perbendaharaan hatinya yang baik dan orang yang jahat mengeluarkan barang yang jahat dari perbendaharaannya yang jahat. Karena yang diucapkan mulutnya, meluap dari hatinya."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 202, '46 "Mengapa kamu berseru kepada-Ku: Tuhan, Tuhan, padahal kamu tidak melakukan apa yang Aku katakan?');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 203, '47 Setiap orang yang datang kepada-Ku dan mendengarkan perkataan-Ku serta melakukannya--Aku akan menyatakan kepadamu dengan siapa ia dapat disamakan--,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 204, '48 ia sama dengan seorang yang mendirikan rumah: Orang itu menggali dalam-dalam dan meletakkan dasarnya di atas batu. Ketika datang air bah dan banjir melanda rumah itu, rumah itu tidak dapat digoyahkan, karena rumah itu kokoh dibangun.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 205, '49 Akan tetapi barangsiapa mendengar perkataan-Ku, tetapi tidak melakukannya, ia sama dengan seorang yang mendirikan rumah di atas tanah tanpa dasar. Ketika banjir melandanya, rumah itu segera rubuh dan hebatlah kerusakannya."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 206, '');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 207, 'Kisah Para Rasul 20');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 208, '');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(29, 209, '35 Dalam segala sesuatu telah kuberikan contoh kepada kamu, bahwa dengan bekerja demikian kita harus membantu orang-orang yang lemah dan harus mengingat perkataan Tuhan Yesus, sebab Ia sendiri telah mengatakan: Adalah lebih berbahagia memberi dari pada menerima."');
