INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 1, '74. Menenangkan Badai');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 1, '(Markus 4:35-41, <i>Matius 8:18; 23-27</i>, <b>Lukas 8:22-25</b>)');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 3, '35 Pada hari itu, waktu hari sudah petang <i>ketika Yesus melihat orang banyak mengelilingi-Nya</i>, Yesus berkata kepada mereka: "Marilah kita bertolak ke seberang <b>danau</b>."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 4, '36 Mereka meninggalkan orang banyak itu lalu bertolak dan membawa Yesus beserta dengan mereka dalam perahu <i>(dan murid-murid-Nyapun mengikuti-Nya)</i> di mana Yesus telah duduk dan perahu-perahu lain juga menyertai Dia.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 26, '<b>23 Dan ketika mereka sedang berlayar, Yesus tertidur. Sekonyong-konyong turunlah taufan ke danau, sehingga perahu itu kemasukan air dan mereka berada dalam bahaya.</b>');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 5, '37 Lalu <b>sekonyong-konyong</b> mengamuklah taufan yang sangat dahsyat <b>ke danau sehingga perahu itu kemasukan air</b> dan ombak menyembur masuk ke dalam perahu, sehingga perahu itu mulai penuh dengan air <b>dan mereka berada dalam bahaya</b>.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 6, '38 Pada waktu itu Yesus sedang tidur di buritan di sebuah tilam. Maka <i>datanglah</i> murid-murid-Nya membangunkan Dia dan berkata kepada-Nya: "Guru, Engkau tidak perduli kalau kita binasa?" <b>(Tuhan, tolonglah, kita binasa.)</b>');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 7, '39 Iapun bangun, <i>Ia berkata kepada mereka: "Mengapa kamu takut, kamu yang kurang percaya?"</i> [dan] menghardik angin <b>dan air yang mengamuk</b> itu dan berkata kepada danau itu: "Diam! Tenanglah!" Lalu angin itu reda dan danau itu menjadi teduh sekali.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 8, '40 Lalu Ia berkata kepada mereka: "Mengapa kamu begitu takut? <b>Di manakah kepercayaanmu?</b> Mengapa kamu tidak percaya?"');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 9, '41 <i>Lalu heranlah orang-orang itu</i>, mereka menjadi sangat takut dan berkata seorang kepada yang lain: "Siapa gerangan orang ini, sehingga angin dan danaupun taat kepada-Nya?"');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 9, '75. Yesus Menyembuhkan Orang-orang Gerada yang Kerasukan');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 9, '(Markus 5:1-21, <i>Matius 8:28-34</i>, <b>Lukas 8:26-40</b>)');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 3, '1 Lalu sampailah mereka di seberang danau, <b>lalu mendaratlah Yesus dan murid-muridNya</b> di daerah orang Gerasa <b>(Gadara)</b>.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 4, '2 Baru saja Yesus turun dari perahu [dan] <b>naik ke darat</b>, datanglah seorang <b>laki-laki</b> <i>(dua orang yang kerasukan setan)</i> yang kerasukan roh jahat dari <b>(kota)</b> pekuburan menemui Dia.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 38, '<b>27 ...Orang itu dirasuki oleh setan-setan dan sudah lama ia tidak berpakaian dan tidak tinggal dalam rumah, tetapi dalam pekuburan.</b>');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 5, '3 Orang itu diam di sana dan tidak ada seorangpun lagi yang sanggup mengikatnya, sekalipun dengan rantai,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 6, '4 karena sudah sering ia dibelenggu dan dirantai, tetapi rantainya diputuskannya dan belenggunya dimusnahkannya, sehingga tidak ada seorangpun yang cukup kuat untuk menjinakkannya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 27, '<i>28 ...Mereka sangat berbahaya, sehingga tidak seorangpun yang berani melalui jalan itu.</i>');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 7, '5 Siang malam ia berkeliaran di pekuburan dan di bukit-bukit sambil berteriak-teriak dan memukuli dirinya dengan batu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 8, '6 Ketika ia melihat Yesus dari jauh, berlarilah ia mendapatkan-Nya lalu <b>tersungkurlah di hadapanNya dan</b> menyembah-Nya,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 9, '7 dan dengan keras ia berteriak: "Apa urusan-Mu dengan aku, hai Yesus, Anak Allah Yang Mahatinggi? <i>Adakah Engkau kemari untuk menyiksa kami sebelum waktunya?</i> Demi Allah, <b>aku mohon kepadaMu</b> jangan siksa aku!"');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 10, '8 Karena sebelumnya Yesus mengatakan kepadanya: "Hai engkau roh jahat! Keluar dari orang ini!"');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 40, '<b>29 ...Karena sering roh itu menyeret-nyeret dia, maka untuk menjaganya, ia dirantai dan dibelenggu, tetapi ia memutuskan segala pengikat itu dan ia dihalau oleh setan itu ke tempat-tempat yang sunyi.</b>');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 11, '9 Kemudian Ia bertanya kepada orang itu: "Siapa namamu?" Jawabnya: "Namaku Legion, karena kami banyak."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 11, '<b>Karena ia kerasukan banyak setan.</b>');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 12, '10 Ia memohon dengan sangat supaya Yesus jangan mengusir roh-roh itu keluar dari daerah itu, <b>supaya Ia jangan memerintahkan mereka masuk ke dalam jurang maut</b>');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 13, '11 <i>Tidak jauh dari mereka </i> adalah di sana di lereng bukit sejumlah besar babi sedang mencari makan,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 14, '12 lalu roh-roh itu meminta kepada-Nya, katanya: "<i>Jika Engkau mengusir kami</i>, suruhlah kami pindah ke dalam babi-babi itu, biarkanlah kami memasukinya!" <i>Yesus berkata pada mereka: “Pergilah.”</i>');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 15, '13 Yesus mengabulkan permintaan mereka. Lalu keluarlah roh-roh jahat itu <b>dari orang itu</b> dan memasuki babi-babi itu. <b>Seluruh</b> kawanan babi yang kira-kira dua ribu jumlahnya itu terjun dari tepi jurang ke dalam danau dan mati lemas di dalamnya <i>(mati di dalam air)</i>.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 16, '14 <b>Setelah penjaga-penjaga babi itu melihat apa yang telah terjadi</b>, maka larilah penjaga-penjaga babi itu dan setibanya <i>di kota dan</i> menceriterakan hal itu <i>(segala sesuatu juga tentang orang-orang yang kerasukan setan itu)</i> di kota dan di kampung-kampung sekitarnya. Lalu keluarlah orang untuk melihat apa yang terjadi.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 17, '15 Mereka <i>(seluruh kota)</i> datang kepada Yesus dan <b>(menjumpai)</b> melihat orang yang kerasukan itu duduk, sudah berpakaian dan sudah waras, orang yang tadinya kerasukan legion itu. Maka takutlah mereka.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 18, '16 Orang-orang yang telah melihat sendiri hal itu menceriterakan kepada mereka tentang apa yang telah terjadi atas orang yang kerasukan setan itu <b>(bagaimana orang yang dirasuk setan itu telah diselamatkan)</b>, dan tentang babi-babi itu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 19, '17 Lalu mereka, <b>seluruh penduduk daerah Gerasa</b>, <i>mendapatkan Yesus</i>, <b>meminta</b> <i>dan</i> mendesak Yesus supaya Ia meninggalkan daerah mereka.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 20, '18 Pada waktu Yesus naik lagi ke dalam perahu, orang yang tadinya kerasukan setan itu meminta, supaya ia diperkenankan menyertai Dia.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 21, '19 Yesus tidak memperkenankannya, tetapi Ia <b>menyuruh dia pergi dan</b> berkata kepada orang itu: "Pulanglah ke rumahmu, kepada orang-orang sekampungmu, dan beritahukanlah kepada mereka segala sesuatu yang telah diperbuat oleh Tuhan atasmu dan bagaimana Ia telah mengasihani engkau!"');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 22, '20 Orang itupun pergilah <b>dan mengelilingi seluruh kota</b> dan mulai memberitakan di daerah Dekapolis segala apa yang telah diperbuat Yesus atas dirinya dan mereka semua menjadi heran.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 22, '<i>Naiklah Yesus ke dalam perahu lalu menyeberang.</i>');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 23, '21 Sesudah Yesus menyeberang lagi dengan perahu, <i>kemudian sampailah Ia ke kotaNya sendiri</i> [kampung halamanNya], <i>dan</i> orang banyak berbondong-bondong datang lalu mengerumuni Dia <b>dan orang banyak menyambut Dia sebab mereka semua menanti-nantikan Dia</b>. Sedang Ia berada di tepi danau,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 23, '76. Yesus Membangkitkan Puteri Yairus dan Menyembuhkan Seorang Perempuan yang Sakit Pendarahan');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 23, '(Markus 5:22-43, <i>Matius 9:18-26</i>, <b>Lukas 8:41-56</b>)');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 28, '<i>18 Sementara Yesus berbicara demikian kepada mereka,...</i>');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 3, '22 datanglah seorang kepala rumah ibadat yang bernama Yairus. Ketika ia melihat Yesus, tersungkurlah ia di depan kaki-Nya <i>lalu menyembah Dia</i>');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 4, '23 dan memohon dengan sangat kepada-Nya: <b>supaya Yesus datang ke rumahnya, karena anaknya perempuan yang satu-satunya, yang berumur kira-kira dua belas tahun, hampir mati:</b> "Anakku perempuan sedang sakit, hampir mati, datanglah kiranya dan letakkanlah tangan-Mu atasnya, supaya ia selamat dan tetap hidup."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 5, '24 Lalu pergilah Yesus dengan orang itu <i>bersama-sama dengan murid-muridNya</i>. Orang banyak berbondong-bondong mengikuti Dia dan berdesak-desakan di dekat-Nya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 6, '25 Adalah di situ seorang perempuan yang sudah dua belas tahun lamanya menderita pendarahan.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 7, '26 Ia telah berulang-ulang diobati oleh berbagai tabib, sehingga telah dihabiskannya semua yang ada padanya, namun sama sekali tidak ada faedahnya malah sebaliknya keadaannya makin memburuk.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 8, '27 Dia sudah mendengar berita-berita tentang Yesus, maka di tengah-tengah orang banyak itu ia mendekati Yesus dari belakang dan menjamah <i>jumbai</i> jubah-Nya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 9, '28 Sebab katanya <i>dalam hatinya</i>: "Asal kujamah saja jubah-Nya, aku akan sembuh."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 10, '29 Seketika itu juga berhentilah pendarahannya dan ia merasa, bahwa badannya sudah sembuh dari penyakitnya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 10, '<i>Maka sejak saat itu sembuhlah perempuan itu.</i>');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 11, '30 Pada ketika itu juga Yesus mengetahui, bahwa ada tenaga yang keluar dari diri-Nya, lalu Ia berpaling di tengah orang banyak dan bertanya: "Siapa yang menjamah jubah-Ku?"');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 12, '31 <b>Dan karena tidak ada yang mengakuinya, Petrus dan</b> murid-murid-Nya menjawab: "<b>Guru</b>, Engkau melihat bagaimana orang-orang ini berdesak-desakan dekat-Mu <b>(mengerumumi dan mendesak Engkau)</b>, dan Engkau bertanya: Siapa yang menjamah Aku?"');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 45, '<b>46 Tetapi Yesus berkata: "Ada seorang yang menjamah Aku, sebab Aku merasa ada kuasa keluar dari diri-Ku."</b>');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 13, '32 Lalu Ia memandang sekeliling-Nya untuk melihat siapa yang telah melakukan hal itu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 13, '<i>Tetapi Yesus berpaling dan memandang dia.</i>');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 14, '33 Perempuan itu, yang menjadi takut dan gemetar ketika mengetahui apa yang telah terjadi atas dirinya <b>bahwa perbuatannya itu ketahuan</b>, ia tampil <b>dengan gementar</b> dan tersungkur di depan Yesus dan dengan tulus memberitahukan segala sesuatu kepada-Nya <b>dan menceritakan kepada orang banyak apa sebabnya ia menjamah Dia dan bahwa ia seketika itu juga menjadi sembuh</b>.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 15, '34 Maka <b>Yesus memandang dia</b> dan kata-Nya kepada perempuan itu: "Hai anak-Ku, <b>teguhkanlah hatimu</b>, imanmu telah menyelamatkan engkau. Pergilah dengan selamat dan sembuhlah dari penyakitmu!"');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 16, '35 Ketika Yesus masih berbicara datanglah orang dari keluarga kepala rumah ibadat itu dan berkata: "Anakmu sudah mati, apa perlunya lagi engkau menyusah-nyusahkan Guru?"');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 17, '36 Tetapi Yesus tidak menghiraukan perkataan mereka dan berkata kepada kepala rumah ibadat: "Jangan takut, percaya saja, <b>dan anakmu akan selamat</b>!"');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 18, '37 Lalu Yesus tidak memperbolehkan seorangpun ikut serta, kecuali Petrus, Yakobus dan Yohanes, saudara Yakobus <b>dan ayah anak itu serta ibunya</b>.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 19, '38 Mereka tiba di rumah kepala rumah ibadat, dan di sana dilihat-Nya orang-orang ribut, <i>dan melihat peniup-peniup seruling</i> menangis dan meratap dengan suara nyaring.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 20, '39 Sesudah Ia masuk Ia berkata kepada orang-orang itu: "Mengapa kamu ribut dan menangis? <b>Jangan menangis</b> <i>Pergilah</i>, anak ini tidak mati, tetapi tidur!"');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 21, '40 Tetapi mereka menertawakan Dia <b>karena mereka tahu bahwa anak itu telah mati</b>. Maka diusir-Nya semua orang itu, lalu dibawa-Nya ayah dan ibu anak itu dan mereka yang bersama-sama dengan Dia masuk ke kamar anak itu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 22, '41 Lalu dipegang-Nya tangan anak itu, kata-Nya: "Talita kum," yang berarti: "Hai anak, Aku berkata kepadamu, bangunlah!"');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 23, '42 <b>Maka kembalilah roh anak itu dan</b> seketika itu juga anak itu bangkit berdiri dan berjalan, sebab umurnya sudah dua belas tahun. Semua orang yang hadir <b>(orang tua anak itu)</b> sangat takjub.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 24, '43 Dengan sangat Ia berpesan kepada mereka, <b>Yesus melarang mereka memberitahukan kepada siapun juga apa yang terjadi</b> supaya jangan seorangpun mengetahui hal itu, lalu Ia menyuruh mereka memberi anak itu makan.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 24, '<i>Maka tersiarlah kabar tentang hal itu ke seluruh daerah itu.</i>');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 24, '77. Tuhan Yesus Menyembuhkan 2 Orang Buta');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 24, '(Matius 9:27-31)');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 3, '27 Ketika Yesus meneruskan perjalanan-Nya dari sana, dua orang buta mengikuti-Nya sambil berseru-seru dan berkata: "Kasihanilah kami, hai Anak Daud."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 4, '28 Setelah Yesus masuk ke dalam sebuah rumah, datanglah kedua orang buta itu kepada-Nya dan Yesus berkata kepada mereka: "Percayakah kamu, bahwa Aku dapat melakukannya?" Mereka menjawab: "Ya Tuhan, kami percaya."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 5, '29 Lalu Yesus menjamah mata mereka sambil berkata: "Jadilah kepadamu menurut imanmu."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 6, '30 Maka meleklah mata mereka. Dan Yesuspun dengan tegas berpesan kepada mereka, kata-Nya: "Jagalah supaya jangan seorangpun mengetahui hal ini."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 7, '31 Tetapi mereka keluar dan memasyhurkan Dia ke seluruh daerah itu.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 7, '78. Seorang Bisu Disembuhkan');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 7, '(Matius 9:32-34)');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 3, '32 Sedang kedua orang buta itu keluar, dibawalah kepada Yesus seorang bisu yang kerasukan setan.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 4, '33 Dan setelah setan itu diusir, dapatlah orang bisu itu berkata-kata. Maka heranlah orang banyak, katanya: "Yang demikian belum pernah dilihat orang di Israel."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(33, 5, '34 Tetapi orang Farisi berkata: "Dengan kuasa penghulu setan Ia mengusir setan."');
