INSERT INTO book ('chapter', 'verse', 'content') VALUES(51, 1, '168. Mengutuki Pohon Ara');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(51, 2, '(Markus 11:12-14, <i>Matius 21:18-19<i>)');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(51, 3, '12 Keesokan harinya <i>pada pagi-pagi hari</i> sesudah Yesus dan kedua belas murid-Nya meninggalkan Betania, <i>dalam perjalannya kembali ke kota,</i> Yesus merasa lapar.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(51, 4, '13 Dan dari jauh Ia melihat pohon ara <i>dekat jalan</i> yang sudah berdaun. Ia mendekatinya untuk melihat kalau-kalau Ia mendapat apa-apa pada pohon itu. Tetapi waktu Ia tiba di situ, Ia tidak mendapat apa-apa <i>pada pohon itu</i> selain daun-daun saja, sebab memang bukan musim buah ara.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(51, 5, '14 Maka kata-Nya kepada pohon itu: "<i>Engkau tidak akan berbuah lagi selama-lamanya,</i> jangan lagi seorangpun makan buahmu selama-lamanya!" Dan murid-murid-Nyapun mendengarnya. <i>Dan seketika itu juga keringlah pohon ara itu.</i>');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(51, 6, '169. Untuk Kedua-kalinya Yesus Menyucikan Bait Allah');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(51, 7, '(Markus 11:15-18, <i>Matius 21:12-13</i>, <b>Lukas 19:45-48</b>)');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(51, 8, '15 Lalu tibalah Yesus dan murid-murid-Nya di Yerusalem. Sesudah Yesus masuk ke Bait Allah, mulailah Ia mengusir orang-orang yang berjual beli di halaman Bait Allah. Meja-meja penukar uang dan bangku-bangku pedagang merpati dibalikkan-Nya,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(51, 9, '16 dan Ia tidak memperbolehkan orang membawa barang-barang melintasi halaman Bait Allah.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(51, 10, '17 Lalu Ia mengajar mereka, kata-Nya: "Bukankah ada tertulis: Rumah-Ku akan disebut rumah doa bagi segala bangsa? Tetapi kamu ini telah menjadikannya sarang penyamun!" <b>Tiap-tiap hari Ia mengajar di Bait Allah.</b>');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(51, 11, '18 Imam-imam kepala dan ahli-ahli Taurat <b>serta orang-orang terkemuka dari bangsa Israel</b> mendengar tentang peristiwa itu, dan mereka berusaha untuk membinasakan Dia, <b>tetapi mereka tidak tahu, bagaimana harus melakukannya,</b> sebab mereka takut kepada-Nya, melihat seluruh orang banyak takjub akan pengajaran-Nya <b>dan ingin mendengarkan Dia</b>.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(51, 12, '170. Menyembuhkan Orang Sakit di Bait Allah');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(51, 13, '(Matius 21:14-17, <i>Markus 11:19</i>)');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(51, 14, '14 Maka datanglah orang-orang buta dan orang-orang timpang kepada-Nya dalam Bait Allah itu dan mereka disembuhkan-Nya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(51, 15, '15 Tetapi ketika imam-imam kepala dan ahli-ahli Taurat melihat mujizat-mujizat yang dibuat-Nya itu dan anak-anak yang berseru dalam Bait Allah: "Hosana bagi Anak Daud!" hati mereka sangat jengkel,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(51, 16, '16 lalu mereka berkata kepada-Nya: "Engkau dengar apa yang dikatakan anak-anak ini?" Kata Yesus kepada mereka: "Aku dengar; belum pernahkah kamu baca: Dari mulut bayi-bayi dan anak-anak yang menyusu Engkau telah menyediakan puji-pujian?" <i>Menjelang malam mereka keluar lagi dari kota.</i>');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(51, 17, '17 Lalu Ia meninggalkan mereka dan pergi ke luar kota ke Betania dan bermalam di situ.');
