INSERT INTO book ('chapter', 'verse', 'content') VALUES(57, 1, '214. Kuburan Yesus Dijaga');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(57, 2, '(Matius 27:62-66)');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(57, 3, '62 Keesokan harinya, yaitu sesudah hari persiapan, datanglah imam-imam kepala dan orang-orang Farisi bersama-sama menghadap Pilatus,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(57, 4, '63 dan mereka berkata: "Tuan, kami ingat, bahwa si penyesat itu sewaktu hidup-Nya berkata: Sesudah tiga hari Aku akan bangkit.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(57, 5, '64 Karena itu perintahkanlah untuk menjaga kubur itu sampai hari yang ketiga; jikalau tidak, murid-murid-Nya mungkin datang untuk mencuri Dia, lalu mengatakan kepada rakyat: Ia telah bangkit dari antara orang mati, sehingga penyesatan yang terakhir akan lebih buruk akibatnya dari pada yang pertama."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(57, 6, '65 Kata Pilatus kepada mereka: "Ini penjaga-penjaga bagimu, pergi dan jagalah kubur itu sebaik-baiknya."');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(57, 7, '66 Maka pergilah mereka dan dengan bantuan penjaga-penjaga itu mereka memeterai kubur itu dan menjaganya.');
