INSERT INTO book ('chapter', 'verse', 'content') VALUES(60, 1, '232. Kenaikan Yesus Ke Sorga');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(60, 2, '(Kisah Para Rasul 1:9-12, <i>Markus 16:19-20</i>, <b>Lukas 24:51-53</b>)');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(60, 3, '51 <b>Dan ketika Ia sedang memberkati mereka, Ia berpisah dari mereka dan terangkat ke sorga</b> <i>lalu duduk di sebelah kanan Allah</i>.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(60, 4, '9 Sesudah Ia mengatakan demikian, terangkatlah Ia disaksikan oleh mereka, dan awan menutup-Nya dari pandangan mereka.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(60, 5, '10 Ketika mereka sedang menatap ke langit waktu Ia naik itu, tiba-tiba berdirilah dua orang yang berpakaian putih dekat mereka,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(60, 6, '11 dan berkata kepada mereka: "Hai orang-orang Galilea, mengapakah kamu berdiri melihat ke langit? Yesus ini, yang terangkat ke sorga meninggalkan kamu, akan datang kembali dengan cara yang sama seperti kamu melihat Dia naik ke sorga." <b>Mereka sujud menyembah kepadaNya, dan</b>');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(60, 7, '12 kembalilah rasul-rasul itu ke Yerusalem dari bukit yang disebut Bukit Zaitun, yang hanya seperjalanan Sabat jauhnya dari Yerusalem.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(60, 8, '<b>53 Mereka senantiasa berada di dalam Bait Allah dan memuliakan Allah. Amin.</b>');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(60, 9, '<i>20 Merekapun pergilah memberitakan Injil ke segala penjuru, dan Tuhan turut bekerja dan meneguhkan firman itu dengan tanda-tanda yang menyertainya.</i>');
