INSERT INTO book ('chapter', 'verse', 'content') VALUES(61, 1, '233. Tujuan Injil');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(61, 2, 'Yohanes 20:30-31, <i>21:25</i>)');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(61, 3, '30 Memang masih banyak tanda lain yang dibuat Yesus di depan mata murid-murid-Nya, yang tidak tercatat dalam kitab ini,');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(61, 4, '31 tetapi semua yang tercantum di sini telah dicatat, supaya kamu percaya, bahwa Yesuslah Mesias, Anak Allah, dan supaya kamu oleh imanmu memperoleh hidup dalam nama-Nya.');
INSERT INTO book ('chapter', 'verse', 'content') VALUES(61, 5, '25 Masih banyak hal-hal lain lagi yang diperbuat oleh Yesus, tetapi jikalau semuanya itu harus dituliskan satu per satu, maka agaknya dunia ini tidak dapat memuat semua kitab yang harus ditulis itu.');
